require("dotenv").config();const axios=require("axios"),consumerKey=process.env.CONSUMER_KEY,consumerSecret=process.env.CONSUMER_SECRET;async function testToken(){try{const e=Buffer.from(`${consumerKey}:${consumerSecret}`).toString("base64"),o=await axios.get("https://sandbox.safaricom.co.ke/oauth/v1/generate?grant_type=client_credentials",{headers:{Authorization:`Basic ${e}`}});console.log("Access token:",o.data.access_token)}catch(e){console.error("Error:",e.response?.data||e.message)}}testToken();